import React, { useEffect, useState } from "react";
import LanguageBarChart from "../components/LanguageBarChart";
import LanguageTable from "../components/LanguageTable";
import LanguagePieChart from "../components/LanguagePieChart";
import LanguageComparisonChart from "../components/LanguageComparisonChart";
import "../index.css";

// index.js 또는 App.js
import {
  Chart,
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
} from "chart.js";

// 모든 필요한 컴포넌트를 등록
Chart.register(
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale
);



const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("/data.json")
      .then((response) => response.json())
      .then((jsonData) => setData(jsonData))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

return (
    <div>
      <h2>실시간 언어 트렌드</h2>
      <div className="chart-row">
        <div className="chart-container">
          <LanguagePieChart data={data} />
        </div>
        <div className="chart-container">
          <LanguageBarChart data={data} />
        </div>
        <div className="chart-container">
          <LanguageComparisonChart data={data} />
        </div>
      </div>
    </div>
  );
};

export default App;
