import React, { useEffect, useState } from "react";
import LanguageTable from "../components/LanguageTable";
import LanguagePieChart from "../components/LanguagePieChart";
import LanguageComparisonChart from "../components/LanguageComparisonChart";

// index.js 또는 App.js
import {
  Chart,
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale,
} from "chart.js";

// 모든 필요한 컴포넌트를 등록
Chart.register(
  ArcElement,
  Tooltip,
  Legend,
  BarElement,
  LineElement,
  PointElement,
  CategoryScale,
  LinearScale
);



const App = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    fetch("/data.json")
      .then((response) => response.json())
      .then((jsonData) => setData(jsonData))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div className="main-layout">
      <div className="chart-container">
        <h3 className="section-title">전체 언어 순위</h3>
        <LanguagePieChart data={data} />
      </div>

      {/* 프론트엔드 및 백엔드 언어 순위 */}
      <div className="chart-row">
        <div className="chart-container">
          <h3 className="section-title">프론트엔드 언어 순위</h3>
          <FrontendBarChart data={frontendData} />
        </div>
        <div className="chart-container">
          <h3 className="section-title">백엔드 언어 순위</h3>
          <BackendBarChart data={backendData} />
        </div>
      </div>
    </div>
  );
};

export default App;
