import React from "react";
import { Bar } from "react-chartjs-2";
import "./styles/LanguageTable.css";

const LanguageComparisonChart = ({ data }) => {
  const frontendLanguages = ["JavaScript", "HTML", "CSS", "TypeScript"];
  const backendLanguages = ["Python", "Java", "C#", "Ruby", "PHP"];

  const getLanguageData = (languages) =>
    languages.map(
      (lang) => data.find((item) => item.language === lang)?.trend_score || 0
    );

  const frontendData = getLanguageData(frontendLanguages);
  const backendData = getLanguageData(backendLanguages);

  const createChartData = (labels, values, label, color) => ({
    labels,
    datasets: [
      {
        label: label,
        data: values,
        backgroundColor: color,
        borderColor: color,
        borderWidth: 1,
      },
    ],
  });

  const frontendChartData = createChartData(
    frontendLanguages,
    frontendData,
    "프론트엔드 언어",
    "rgba(75, 192, 192, 0.7)"
  );

  const backendChartData = createChartData(
    backendLanguages,
    backendData,
    "백엔드 언어",
    "rgba(153, 102, 255, 0.7)"
  );

  return (
    <div className="comparison-chart-container">
      <div className="chart-section">
        <h3>프론트엔드 언어 순위</h3>
        <Bar data={frontendChartData} />
      </div>
      <div className="chart-section">
        <h3>백엔드 언어 순위</h3>
        <Bar data={backendChartData} />
      </div>
    </div>
  );
};

export default LanguageComparisonChart;
