import React from "react";
import { Bar } from "react-chartjs-2";
import "./styles/LanguageTable.css";
import "./styles/Chart.css";

const LanguageComparisonChart = ({ data }) => {
  const frontendLanguages = ["JavaScript", "HTML", "CSS"];
  const backendLanguages = ["Python", "Java", "C#"];

  const frontendData = frontendLanguages.map(
    (lang) => data.find((item) => item.language === lang)?.trend_score || 0
  );
  const backendData = backendLanguages.map(
    (lang) => data.find((item) => item.language === lang)?.trend_score || 0
  );

  const chartData = {
    labels: frontendLanguages.concat(backendLanguages),
    datasets: [
      {
        label: "프론트엔드",
        data: frontendData,
        backgroundColor: "rgba(75, 192, 192, 0.5)",
        borderColor: "rgba(75, 192, 192, 1)",
        borderWidth: 1,
      },
      {
        label: "백엔드",
        data: backendData,
        backgroundColor: "rgba(153, 102, 255, 0.5)",
        borderColor: "rgba(153, 102, 255, 1)",
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="comparison-chart-container">
      <h3>프론트엔드 vs 백엔드</h3>
      <Bar data={chartData} />
    </div>
  );
};

export default LanguageComparisonChart;
